import { useState } from "react";
import { Table, Form, Button, Alert } from "react-bootstrap";
import { Routes, Route, Link, Navigate, useNavigate, useMatch } from "react-router-dom";

const Home = () => (
	<div>
		<h2>TKTL notes app</h2>
		<p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
	</div>
);

const Notes = ({ notes }) => (
	<div>
		<h2>Notes</h2>
		<Table striped>
			<tbody>
				{notes.map((note) => (
					<tr key={note.id}>
						<td>
							<Link to={`/notes/${note.id}`}>{note.content}</Link>
						</td>
						<td>{note.user}</td>
					</tr>
				))}
			</tbody>
		</Table>
	</div>
);

const Note = ({ note }) => (
	<div>
		<h2>hello {note.content}</h2>
		<div>{note.user}</div>
		<div>
			<strong>{note.important ? "important" : "not important"}</strong>
		</div>
	</div>
);

const Users = () => (
	<div>
		<h2>TKTL notes app- users</h2>
		<ul>
			<li>Matti Luukkainen</li>
			<li>Juha Tauriainen</li>
			<li>Arto Hellas</li>
		</ul>
	</div>
);

const Login = (props) => {
	const navigate = useNavigate();

	const onSubmit = (e) => {
		e.preventDefault();
		props.onLogin("username");
		navigate("/users");
	};
	return (
		<div>
			<h2>login</h2>
			<Form onSubmit={onSubmit}>
				<Form.Group>
					<Form.Label>username:</Form.Label>
					<Form.Control type="text" name="username" />

					<Form.Label>password:</Form.Label>
					<Form.Control type="password" />

					<Button variant="primary" type="submit">
						login
					</Button>
				</Form.Group>
			</Form>
		</div>
	);
};

const App = () => {
	const [notes, setNotes] = useState([
		{
			id: 1,
			content: "HTML is easy",
			important: true,
			user: "Matti Luukkainen",
		},
		{
			id: 2,
			content: "Browser can execute only JavaScript",
			important: false,
			user: "Matti Luukkainen",
		},
		{
			id: 3,
			content: "Most important methods of HTTP-protocol are GET and POST",
			important: true,
			user: "Arto Hellas",
		},
	]);

	const [user, setUser] = useState(null);
	const match = useMatch("/notes/:id");
	const [message, setMessage] = useState(null);

	//console.log(match);
	//console.log(notes);
	//console.log(notes.find((n) => n.id === Number(match.params.id)));

	const note = match ? notes.find((n) => n.id === Number(match.params.id)) : null;
	const login = (user) => {
		setUser(user);
		setMessage(`welcome ${user}`);
		setTimeout(() => {
			setMessage(null);
		}, 5000);
	};
	const padding = {
		padding: 5,
	};

	return (
		<div className="container">
			{message && <Alert>{message}</Alert>}
			<div>
				<Link style={padding} to="/">
					home
				</Link>

				<Link style={padding} to="/notes">
					notes
				</Link>

				<Link style={padding} to="/users">
					users
				</Link>

				{user ? (
					<em>{user} logged in</em>
				) : (
					<Link style={padding} to="/login">
						login
					</Link>
				)}
			</div>
			<Routes>
				<Route path="/notes/:id" element={<Note note={note} />} />
				<Route path="/notes" element={<Notes notes={notes} />} />
				<Route path="/users" element={user ? <Users /> : <Navigate replace to="/login" />} />
				<Route path="/login" element={<Login onLogin={login} />} />
				<Route path="/" element={<Home />} />
			</Routes>
		</div>
	);
};

export default App;

